import React from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight, Target, Map, TrendingUp, Users, Brain } from 'lucide-react';
import './Home.css';

const Home = () => {
  const features = [
    {
      icon: Brain,
      title: "AI-Powered Analysis",
      description: "Advanced algorithms analyze your skills, interests, and goals to provide personalized career recommendations."
    },
    {
      icon: Map,
      title: "Career Path Mapping",
      description: "Visualize your career journey with detailed roadmaps and milestone tracking for your professional growth."
    },
    {
      icon: Target,
      title: "Skills Gap Analysis",
      description: "Identify the skills you need to develop and get personalized learning recommendations to achieve your goals."
    },
    {
      icon: TrendingUp,
      title: "Market Insights",
      description: "Stay updated with real-time job market trends, salary data, and emerging opportunities in your field."
    },
    {
      icon: Users,
      title: "Industry Connections",
      description: "Connect with professionals in your target field and get mentorship opportunities to accelerate your growth."
    }
  ];

  return (
    <div className="home">
      <div className="container">
        <section className="hero">
          <h1>Your AI-Powered Career Advisor</h1>
          <p>Discover your perfect career path with personalized AI recommendations based on your skills, interests, and the evolving job market.</p>
          <Link to="/assessment" className="btn btn-primary btn-large">
            Start Your Assessment
            <ArrowRight className="btn-icon" />
          </Link>
        </section>

        <section className="features">
          {features.map((feature, index) => {
            const Icon = feature.icon;
            return (
              <div key={index} className="feature-card fade-in">
                <div className="feature-icon">
                  <Icon size={32} />
                </div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            );
          })}
        </section>

        <section className="cta-section">
          <div className="cta-card">
            <h2>Ready to Transform Your Career?</h2>
            <p>Join thousands of students who have found their dream careers with our AI-powered platform.</p>
            <div className="cta-buttons">
              <Link to="/assessment" className="btn btn-primary">
                Take Assessment
              </Link>
              <Link to="/dashboard" className="btn btn-secondary">
                View Demo
              </Link>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
