import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowRight, Save, RotateCcw } from 'lucide-react';
import axios from 'axios';
import './Assessment.css';

const Assessment = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    // Personal Information
    name: '',
    email: '',
    education: '',
    experience: '',

    // Skills Assessment
    technicalSkills: [],
    softSkills: [],

    // Interests
    interests: [],
    workEnvironment: '',
    careerGoals: '',

    // Preferences
    preferredIndustries: [],
    salaryExpectation: '',
    locationPreference: '',
    workLifeBalance: 5
  });

  const technicalSkillsList = [
    'JavaScript', 'Python', 'Java', 'React', 'Node.js', 'SQL', 'MongoDB',
    'AWS', 'Docker', 'Git', 'HTML/CSS', 'TypeScript', 'C++', 'C#',
    'Machine Learning', 'Data Science', 'UI/UX Design', 'Mobile Development'
  ];

  const softSkillsList = [
    'Communication', 'Leadership', 'Problem Solving', 'Teamwork',
    'Creativity', 'Adaptability', 'Time Management', 'Critical Thinking',
    'Emotional Intelligence', 'Conflict Resolution', 'Public Speaking'
  ];

  const industriesList = [
    'Technology', 'Healthcare', 'Finance', 'Education', 'Marketing',
    'Consulting', 'Manufacturing', 'Retail', 'Government', 'Non-profit'
  ];

  const interestsList = [
    'Innovation', 'Helping Others', 'Problem Solving', 'Creative Work',
    'Data Analysis', 'Leadership', 'Learning New Things', 'Working with Teams',
    'Independent Work', 'Customer Interaction', 'Strategic Planning'
  ];

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleArrayToggle = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].includes(value)
        ? prev[field].filter(item => item !== value)
        : [...prev[field], value]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/assessment', formData);
      localStorage.setItem('assessmentResults', JSON.stringify(response.data));
      navigate('/dashboard');
    } catch (error) {
      console.error('Assessment submission error:', error);
      alert('Error submitting assessment. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const nextStep = () => {
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="form-section">
            <h3>Personal Information</h3>
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="Enter your full name"
                required
              />
            </div>
            <div className="form-group">
              <label>Email Address</label>
              <input
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="your.email@example.com"
                required
              />
            </div>
            <div className="form-group">
              <label>Highest Education Level</label>
              <select
                value={formData.education}
                onChange={(e) => handleInputChange('education', e.target.value)}
                required
              >
                <option value="">Select your education level</option>
                <option value="high-school">High School</option>
                <option value="bachelor">Bachelor's Degree</option>
                <option value="master">Master's Degree</option>
                <option value="phd">PhD</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div className="form-group">
              <label>Years of Experience</label>
              <select
                value={formData.experience}
                onChange={(e) => handleInputChange('experience', e.target.value)}
                required
              >
                <option value="">Select experience level</option>
                <option value="0-1">0-1 years (Entry Level)</option>
                <option value="1-3">1-3 years (Junior)</option>
                <option value="3-5">3-5 years (Mid-level)</option>
                <option value="5-10">5-10 years (Senior)</option>
                <option value="10+">10+ years (Expert)</option>
              </select>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="form-section">
            <h3>Technical Skills</h3>
            <p className="section-description">Select all technical skills you possess</p>
            <div className="skills-grid">
              {technicalSkillsList.map(skill => (
                <label key={skill} className="skill-item">
                  <input
                    type="checkbox"
                    checked={formData.technicalSkills.includes(skill)}
                    onChange={() => handleArrayToggle('technicalSkills', skill)}
                  />
                  {skill}
                </label>
              ))}
            </div>

            <h3>Soft Skills</h3>
            <p className="section-description">Select your strongest soft skills</p>
            <div className="skills-grid">
              {softSkillsList.map(skill => (
                <label key={skill} className="skill-item">
                  <input
                    type="checkbox"
                    checked={formData.softSkills.includes(skill)}
                    onChange={() => handleArrayToggle('softSkills', skill)}
                  />
                  {skill}
                </label>
              ))}
            </div>
          </div>
        );

      case 3:
        return (
          <div className="form-section">
            <h3>Interests & Goals</h3>
            <div className="form-group">
              <label>What are your main interests? (Select multiple)</label>
              <div className="skills-grid">
                {interestsList.map(interest => (
                  <label key={interest} className="skill-item">
                    <input
                      type="checkbox"
                      checked={formData.interests.includes(interest)}
                      onChange={() => handleArrayToggle('interests', interest)}
                    />
                    {interest}
                  </label>
                ))}
              </div>
            </div>
            <div className="form-group">
              <label>Preferred Work Environment</label>
              <select
                value={formData.workEnvironment}
                onChange={(e) => handleInputChange('workEnvironment', e.target.value)}
              >
                <option value="">Select preferred environment</option>
                <option value="remote">Remote Work</option>
                <option value="office">Office Environment</option>
                <option value="hybrid">Hybrid (Mix of both)</option>
                <option value="field">Field Work</option>
                <option value="flexible">Flexible</option>
              </select>
            </div>
            <div className="form-group">
              <label>Career Goals</label>
              <textarea
                value={formData.careerGoals}
                onChange={(e) => handleInputChange('careerGoals', e.target.value)}
                placeholder="Describe your short-term and long-term career goals..."
                rows={4}
              />
            </div>
          </div>
        );

      case 4:
        return (
          <div className="form-section">
            <h3>Preferences & Expectations</h3>
            <div className="form-group">
              <label>Preferred Industries</label>
              <div className="skills-grid">
                {industriesList.map(industry => (
                  <label key={industry} className="skill-item">
                    <input
                      type="checkbox"
                      checked={formData.preferredIndustries.includes(industry)}
                      onChange={() => handleArrayToggle('preferredIndustries', industry)}
                    />
                    {industry}
                  </label>
                ))}
              </div>
            </div>
            <div className="form-group">
              <label>Expected Salary Range</label>
              <select
                value={formData.salaryExpectation}
                onChange={(e) => handleInputChange('salaryExpectation', e.target.value)}
              >
                <option value="">Select salary range</option>
                <option value="30-50k">$30,000 - $50,000</option>
                <option value="50-70k">$50,000 - $70,000</option>
                <option value="70-100k">$70,000 - $100,000</option>
                <option value="100-150k">$100,000 - $150,000</option>
                <option value="150k+">$150,000+</option>
              </select>
            </div>
            <div className="form-group">
              <label>Work-Life Balance Priority (1-10)</label>
              <input
                type="range"
                min="1"
                max="10"
                value={formData.workLifeBalance}
                onChange={(e) => handleInputChange('workLifeBalance', parseInt(e.target.value))}
              />
              <div className="range-labels">
                <span>Low Priority</span>
                <span>High Priority</span>
              </div>
            </div>
            <div className="form-group">
              <label>Location Preference</label>
              <input
                type="text"
                value={formData.locationPreference}
                onChange={(e) => handleInputChange('locationPreference', e.target.value)}
                placeholder="e.g., Remote, New York, San Francisco, etc."
              />
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="assessment">
      <div className="container">
        <div className="assessment-header">
          <h1>Career Assessment</h1>
          <p>Help us understand your skills, interests, and goals to provide personalized career recommendations.</p>
        </div>

        <div className="progress-bar">
          <div className="progress-fill" style={{ width: `${(currentStep / 4) * 100}%` }}></div>
        </div>
        <div className="progress-text">
          Step {currentStep} of 4
        </div>

        <form onSubmit={handleSubmit} className="assessment-form">
          {renderStep()}

          <div className="form-navigation">
            <button
              type="button"
              onClick={prevStep}
              disabled={currentStep === 1}
              className="btn btn-secondary"
            >
              <RotateCcw size={16} />
              Previous
            </button>

            {currentStep < 4 ? (
              <button
                type="button"
                onClick={nextStep}
                className="btn btn-primary"
              >
                Next
                <ArrowRight size={16} />
              </button>
            ) : (
              <button
                type="submit"
                disabled={loading}
                className="btn btn-primary"
              >
                {loading ? (
                  <div className="spinner"></div>
                ) : (
                  <>
                    Get Recommendations
                    <Save size={16} />
                  </>
                )}
              </button>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default Assessment;
